function changeColor(a){document.getElementById("main_article").style.color=a}function submitValues(){var a=document.getElementById("form-name").value;var b=document.getElementById("form-email").value;var c="";if(window.XMLHttpRequest){xmlhttp=new XMLHttpRequest}else{xmlhttp=new ActiveXObject("Microsoft.XMLHTTP")}xmlhttp.onreadystatechange=function(){if(xmlhttp.readyState==4&&xmlhttp.status==200){c=xmlhttp.responseText;if(c=="Thank you"){document.getElementById("form-name").disabled=true;document.getElementById("form-email").disabled=true;document.getElementById("form-submit").disabled=true}document.getElementById("response").innerHTML=c}};xmlhttp.open("GET","gt.php?name="+a+"&email="+b,true);xmlhttp.send()}setInterval(function(){var a=new Date;var b=a.getHours();var c=a.getMinutes();var d=a.getSeconds();document.getElementById("clock").innerHTML=b+":"+c+":"+d},1e3);document.getElementById("black").addEventListener("click",function(){changeColor("black")});document.getElementById("red").addEventListener("click",function(){changeColor("red")});document.getElementById("blue").addEventListener("click",function(){changeColor("blue")});document.getElementById("contact").addEventListener("submit",submitValues)